mapper.update:
update t_bullet_msg_sensitive
set sensitive_msg = #{sensitiveMsg},
    show_msg      = #{showMsg},
    create_time   = #{createTime},
    update_time   = #{updateTime}
where id = #{id}

mapper.select:
select id, sensitive_msg, show_msg, create_time, update_time
from t_bullet_msg_sensitive
where id = #{id}
